import React from 'react';
import '../static/styles/HouseDetails.css'; // Import the CSS file for styling

interface HouseDetailsProps {
    house: {
        id: number;
        title: string;
        description: string;
        image: string;
        price: number;
        location: string;
    };
}

const HouseDetails: React.FC<HouseDetailsProps> = ({ house }) => {
    return (
        <div className="house-details">
            <h1>{house.title}</h1>
            <img src={house.image} alt={house.title} />
            <p>{house.description}</p>
            <p>Price: ${house.price}</p>
            <p>Location: {house.location}</p>
        </div>
    );
};

export default HouseDetails;
