import React, { useState } from 'react';
import { House } from '../types/House'; // Import the House type

interface CreateHouseFormProps {
    onCreate: (houseData: Omit<House, 'id'>) => void; // Exclude 'id' since it will be generated
}

const CreateHouseForm: React.FC<CreateHouseFormProps> = ({ onCreate }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [image, setImage] = useState('');
    const [price, setPrice] = useState<number | ''>('');
    const [location, setLocation] = useState('');
    const [error, setError] = useState<string | null>(null); // State for validation errors

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();

        // Validation
        if (!title || !description || !image || !price || !location) {
            setError('All fields are required.');
            return;
        }

        if (isNaN(Number(price)) || Number(price) <= 0) {
            setError('Price must be a valid positive number.');
            return;
        }

        setError(null); // Clear any previous errors

        const newHouse: Omit<House, 'id'> = {
            title,
            description,
            image,
            price: Number(price),
            location,
        };

        onCreate(newHouse);

        // Reset form fields
        setTitle('');
        setDescription('');
        setImage('');
        setPrice('');
        setLocation('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Create a New House</h2>
            {error && <p className="error-message">{error}</p>} {/* Display validation errors */}
            <div>
                <label>Title:</label>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Enter house title"
                />
            </div>
            <div>
                <label>Description:</label>
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Enter house description"
                />
            </div>
            <div>
                <label>Image URL:</label>
                <input
                    type="text"
                    value={image}
                    onChange={(e) => setImage(e.target.value)}
                    placeholder="Enter image URL"
                />
            </div>
            <div>
                <label>Price:</label>
                <input
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value ? Number(e.target.value) : '')}
                    placeholder="Enter price"
                />
            </div>
            <div>
                <label>Location:</label>
                <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="Enter location"
                />
            </div>
            <button type="submit">Create House</button>
        </form>
    );
};

export default CreateHouseForm;
