import { House } from '../types/House';

const houses: House[] = [
    {
        id: 1,
        title: 'Beautiful Family House',
        image: 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.houseplans.net%2Fnews%2Fsimple-house-plans%2F&psig=AOvVaw1JqJ7yyoNP5OIts-IFp_ku&ust=1745401937669000&source=images&cd=vfe&opi=89978449&ved=0CBUQjRxqFwoTCNj8nYav64wDFQAAAAAdAAAAABAE',
        description: 'A lovely family house with a big garden.',
        price: 300000,
        location: "123 Main St, Springfield",
    },
    {
        id: 2,
        title: 'Modern Apartment',
        image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTapw_XWWJYkuY2JzBMXZsmTWtaGHL3p60_kw&s',
        description: 'A modern apartment in the city center.',
        price: 250000,
        location: "124 Main St, Springfield",
    },
    {
        id: 3,
        title: 'Cozy Cottage',
        image: 'https://example.com/house3.jpg',
        description: 'A cozy cottage in the countryside.',
        price: 200000,
        location: "125 Main St, Springfield",
    },
];

export const getHouses = async (): Promise<House[]> => {
    return new Promise((resolve) => {
        setTimeout(() => {
            resolve(houses);
        }, 1000);
    });
};

export const getHouseById = async (id: number): Promise<House | undefined> => {
    return new Promise((resolve) => {
        setTimeout(() => {
            const house = houses.find(h => h.id === id);
            resolve(house);
        }, 1000);
    });
};

export const createHouse = async (newHouse: House): Promise<House> => {
    return new Promise((resolve) => {
        setTimeout(() => {
            const createdHouse = { ...newHouse, id: houses.length + 1 };
            houses.push(createdHouse);
            resolve(createdHouse);
        }, 1000);
    });
};
