import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../static/styles/HouseDetails.css';

interface HouseDetailsProps {
    house: {
        id: number;
        title: string;
        description: string;
        image: string;
        price: number;
        location: string;
    };
}

const HouseDetails: React.FC<HouseDetailsProps> = ({ house }) => {
    const navigate = useNavigate();
    const [isEditing, setIsEditing] = useState(false); // State to toggle edit mode
    const [title, setTitle] = useState(house.title);
    const [description, setDescription] = useState(house.description);
    const [price, setPrice] = useState(house.price);

    const handleBackClick = () => {
        navigate('/'); // Navigate back to the HomePage
    };

    const handleEditClick = () => {
        setIsEditing(true); // Enable edit mode
    };

    const handleSaveClick = async () => {
        try {
            const updatedHouse = {
                ...house,
                title,
                description,
                price,
            };

            const response = await fetch(`http://localhost:5000/houses/${house.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedHouse),
            });

            if (!response.ok) {
                throw new Error('Failed to update house details.');
            }

            setIsEditing(false); // Exit edit mode
            alert('House details updated successfully!');
        } catch (error) {
            console.error('Error updating house details:', error);
            alert('Failed to update house details. Please try again.');
        }
    };

    return (
        <div className="house-details">
            {isEditing ? (
                <div className="edit-form">
                    <h1>Edit House Details</h1>
                    <div className="form-group">
                        <label>Title:</label>
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                    </div>
                    <div className="form-group">
                        <label>Description:</label>
                        <textarea
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </div>
                    <div className="form-group">
                        <label>Price:</label>
                        <input
                            type="number"
                            value={price}
                            onChange={(e) => setPrice(Number(e.target.value))}
                        />
                    </div>
                    <button className="save-button" onClick={handleSaveClick}>
                        Save
                    </button>
                    <button className="cancel-button" onClick={() => setIsEditing(false)}>
                        Cancel
                    </button>
                </div>
            ) : (
                <>
                    <h1>{house.title}</h1>
                    <img
                        className="house-details-image"
                        src={house.image}
                        alt={house.title}
                    />
                    <p>{house.description}</p>
                    <p>Price: ${house.price}</p>
                    <p>Location: {house.location}</p>
                    <button className="edit-button" onClick={handleEditClick}>
                        Edit
                    </button>
                    <button className="back-button" onClick={handleBackClick}>
                        Back to Home
                    </button>
                </>
            )}
        </div>
    );
};

export default HouseDetails;
