import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import '../static/styles/HouseCard.css'; // Import the CSS file for styling

interface HouseCardProps {
    house: {
        id: number;
        title: string;
        image: string;
        description: string;
    };
}

const HouseCard: React.FC<HouseCardProps> = ({ house }) => {
    const navigate = useNavigate(); // Initialize the navigate function

    const handleViewDetails = () => {
        navigate(`/houses/${house.id}`); // Navigate to the house details page
    };

    return (
        <div className="house-card">
            <img className="house-card-image" src={house.image} alt={house.title} />
            <h2>{house.title}</h2>
            <p>{house.description}</p>
            <button onClick={handleViewDetails}>View Details</button> {/* Add onClick to navigate */}
        </div>
    );
};

export default HouseCard;
